import { Fragment, useState } from "react"
import React from 'react'
import e from "cors";


    
function Login() {
    const [first_name,setFirst_name]=useState("");
    const [last_name,setLast_name]=useState("");

    const onSubmit = async e=>{

        e.preventDefault();
        try {
            const body={first_name,last_name};
            const response = await fetch("http://localhost:5000/register",{
                method:"Post",
                headers:{"Content-Type":"application/json"},
                body:JSON.stringify(body)
            })
            console.log(response);
        } catch (err) {
            console.error(err.message);
            
        }

    }
  return (
    <Fragment>
        <h1 className="text-center mt-5 text-monospace">Log In</h1>
        <form onSubmit={onSubmit} >
            <div className="form-group">
                <label htmlFor="exampleInputEmail1">First Name</label>
                <input type="text" className="form-control" id="exampleInputFirstName1" aria-describedby="emailHelp" placeholder="Enter First Name"
                 value={first_name} 
                 onChange={e=>setFirst_name(e.target.value)} />
                
                                </div>
            <div className="form-group">
                <label htmlFor="exampleInputPassword1">Last Name</label>
                <input type="text" className="form-control" id="exampleInputLastName1" placeholder="Last Name" 
                value={last_name} 
                onChange={e=>setLast_name(e.target.value)}/>
                                </div>
            <div className="form-group">
                <label htmlFor="exampleInputEmail1">Email</label>
                <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter Email"/>
                <small id="emailHelp" className="form-text text-muted">We'll never share your email with anyone else.</small>
                
                                </div>
                <button type="submit" className="btn btn-primary">Submit</button>

        </form>
    
    
    
    
    
    </Fragment>
  )
}

export default Login
