import { Fragment, useState } from "react"
import React from 'react'
import CryptoJS from "crypto-js";





function Login() {
    const [first_name,setFirst_name]=useState("");
    const [last_name,setLast_name]=useState("");
    const [email,setEmail]=useState("");
    const [password , setPassword] = useState("");
   
 
    const onSubmit = async e=>{
        function encrypt()
{
var pass=document.getElementById('password').value;
var hide=document.getElementById('hide').value;
if(pass=="")
{
document.getElementById('err').innerHTML='Error:Password is missing';
return false;
}
else
{
document.getElementById("hide").value = document.getElementById("password").value;
var hash = CryptoJS.MD5(pass);
document.getElementById('password').value=hash;
return true;
}
}

        e.preventDefault();
        try {

       
  
            const body={first_name,last_name,email,password};
            const response = await fetch("http://localhost:5000/register",{
                method:"Post",
                headers:{"Content-Type":"application/json"},
                body:JSON.stringify(body)
            })
            console.log(response);
        } catch (err) {
            console.error(err.message);
            
        }
    
    };
      
  return (
    
    <Fragment>
        <h1 className="text-center mt-5 text-monospace">SignUp</h1>
        <form onSubmit={onSubmit } className="login">
            <div className="form-group">
                <label htmlFor="exampleInputFirstName">First Name</label>
                <input type="text" className="form-control" id="exampleInputFirstName1" aria-describedby="emailHelp" placeholder="Enter First Name"
                 value={first_name} 
                 onChange={e=>setFirst_name(e.target.value)} />
                
                                </div>
            <div className="form-group">
                <label htmlFor="exampleInputLastName1">Last Name</label>
                <input type="text" className="form-control" id="exampleInputLastName1" placeholder="Last Name" value={last_name} onChange={e=>setLast_name(e.target.value)}/>
                                </div>
            <div className="form-group">
                <label htmlFor="exampleInputEmail1">Email</label>
                <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter Email" value={email} onChange={e=>setEmail(e.target.value)}/>
                <small id="emailHelp" className="form-text text-muted">We'll never share your email with anyone else.</small>
                
                                </div>
                
                <div className="form-group">
                    <label htmlFor="exampleInputPassword1">Password</label>
                    <input type="password" className="password" id="" placeholder="Enter Password" value={password} onChange={e=>setPassword(e.target.value)}/>
                </div>
                
                <button type="submit" className="btn btn-primary">Submit</button>
        </form>
    
    
    
    
    
    </Fragment>
  )
}

export default Login
