import { Fragment, useState } from "react"
import React from 'react'

function login() {
    const [name,setName]=useState;
    const onSubmitForm = async (e)=>{
        e.preventDefault();
        try {
            const body={name};
            const response = fetch("http://localhost:5000/register",{
                method:"Post",
                headers:{"Content-Type":"application/json"},
                body:JSON.stringify(body)
            })
            console.log(response);
        } catch (err) {
            console.error(err.message);
            
        }
    }
  return (
    <Fragment>
        <h1 className="text-center mt-5 text-monospace">Log In</h1>
        <form onSubmitForm={onSubmitForm}>
            <div class="form-group">
                <label for="exampleInputEmail1">First Name</label>
                <input type="text" class="form-control" id="exampleInputFirstName1" aria-describedby="emailHelp" placeholder="Enter First Name" value={name} onChange={(e)=>{setName(e.target.value)}} />
                
                                </div>
            <div class="form-group">
                <label for="exampleInputPassword1">Last Name</label>
                <input type="text" class="form-control" id="exampleInputLastName1" placeholder="Last Name"/>
                                </div>
            <div class="form-group">
                <label for="exampleInputEmail1">Email</label>
                <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter Email"/>
                <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
                
                                </div>
                <button type="submit" class="btn btn-primary">Submit</button>

        </form>
    
    
    
    
    
    </Fragment>
  )
}

export default login
